{
  "$schema": "http://json-schema.org/draft-04/schema#",
  "type": "object",
  "description": "ISO 20022 JSON Schema GetPaymentTransactionDetailsRequest (camt.a02.001.03) Generated by SWIFT Standards 2018-07-16 09:05:02",
  "additionalProperties": false,
  "properties": {
    "$id": {
      "default": "urn:swift:json:camt.a02.001.03"
    },
    "get_payment_transaction_details_request": {
      "$ref": "#/definitions/GetPaymentTransactionDetailsRequest"
    }
  },
  "definitions": {
    "GetPaymentTransactionDetailsRequest": {
      "type": "object",
      "description": "This API is a payment query to get detailed information regarding a given payment transaction. It requires the UETR to be known.\r\nExamples of use cases are:\r\n•\tDebtor status check - debtor\u0027s agent or debtor wants to see the status\r\n•\tCreditor status check - creditor\u0027s agent or creditor wants to see the status\r\n",
      "additionalProperties": false,
      "properties": {
        "my_institution": {
          "type": "array",
          "maxItems": 250,
          "description": "Specifies the institution which payments are requested.",
          "items": {
            "$ref": "#/definitions/AnyBICIdentifier"
          }
        },
        "uetr": {
          "description": "Contains the unique end to end transaction reference (UETR) of a payment.",
          "$ref": "#/definitions/UUIDv4Identifier"
        }
      },
      "required": [
        "my_institution",
        "uetr"
      ]
    },
    "AnyBICIdentifier": {
      "type": "string",
      "description": "Code allocated to a financial or non-financial institution by the ISO 9362 Registration Authority, as described in ISO 9362 \"Banking - Banking telecommunication messages - Business identifier code (BIC)\".",
      "pattern": "^[A-Z]{6,6}[A-Z2-9][A-NP-Z0-9]([A-Z0-9]{3,3}){0,1}$"
    },
    "UUIDv4Identifier": {
      "type": "string",
      "description": "Universally Unique IDentifier (UUID) version 4, as described in IETC RFC 4122 \"Universally Unique IDentifier (UUID) URN Namespace\".",
      "pattern": "^[a-f0-9]{8}-[a-f0-9]{4}-4[a-f0-9]{3}-[89ab][a-f0-9]{3}-[a-f0-9]{12}$"
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-04/schema#",
  "type": "object",
  "description": "ISO 20022 JSON Schema GetPaymentTransactionDetailsResponse (camt.a02.002.03) Generated by SWIFT Standards 2018-07-16 09:05:02",
  "additionalProperties": false,
  "properties": {
    "$id": {
      "default": "urn:swift:json:camt.a02.002.03"
    },
    "get_payment_transaction_details_response": {
      "$ref": "#/definitions/GetPaymentTransactionDetailsResponse"
    }
  },
  "definitions": {
    "GetPaymentTransactionDetailsResponse": {
      "type": "object",
      "description": "This API is the response to a payment query to get detailed information regarding a given payment transaction.\r\nExamples of use cases are:\r\n•\tDebtor status check - debtor\u0027s agent or debtor wants to see the status\r\n•\tCreditor status check - creditor\u0027s agent or creditor wants to see the status\r\n",
      "additionalProperties": false,
      "properties": {
        "uetr": {
          "description": "Contains the unique end to end transaction reference of a payment.",
          "$ref": "#/definitions/UUIDv4Identifier"
        },
        "transaction_status": {
          "description": "Specifies the status of a transaction, in a coded form.",
          "$ref": "#/definitions/PaymentStatus3"
        },
        "cancellation_status": {
          "$ref": "#/definitions/PaymentTransactionCancellationStatus1"
        },
        "initiation_time": {
          "description": "Date and Time at which the message enters the tracking system (e.g. gpi).",
          "$ref": "#/definitions/ISODateTime"
        },
        "completion_time": {
          "description": "Time at which the Instructed Bank reports that the transaction has been completed. \r\nDate and time based on the creation date of the status confirmation containing a final status ACSC",
          "$ref": "#/definitions/ISODateTime"
        },
        "last_update_time": {
          "description": "Last date and time at which the status of this transaction was updated.",
          "$ref": "#/definitions/ISODateTime"
        },
        "payment_event": {
          "type": "array",
          "description": "This groups the information of an event, i.e., of a payment message or status confirmation update. It is repeated as many times as there are events to be returned.",
          "items": {
            "$ref": "#/definitions/PaymentEvent7"
          }
        }
      },
      "required": [
        "uetr",
        "transaction_status",
        "initiation_time",
        "last_update_time"
      ]
    },
    "UUIDv4Identifier": {
      "type": "string",
      "description": "Universally Unique IDentifier (UUID) version 4, as described in IETC RFC 4122 \"Universally Unique IDentifier (UUID) URN Namespace\".",
      "pattern": "^[a-f0-9]{8}-[a-f0-9]{4}-4[a-f0-9]{3}-[89ab][a-f0-9]{3}-[a-f0-9]{12}$"
    },
    "PaymentStatus3": {
      "type": "object",
      "description": "Indicates the payment transaction status and optionally the reason for that status. ",
      "additionalProperties": false,
      "properties": {
        "status": {
          "description": "Reason for the status, as published in an external reason code list.",
          "$ref": "#/definitions/TransactionIndividualStatus4Code"
        },
        "reason": {
          "description": "Provides more details on the status in process.",
          "$ref": "#/definitions/PaymentReason1Code"
        }
      },
      "required": [
        "status"
      ]
    },
    "TransactionIndividualStatus4Code": {
      "type": "string",
      "description": "Specifies the status of a single payment transaction.",
      "enum": [
        "RJCT",
        "ACSP",
        "ACSC",
        "ACCC"
      ]
    },
    "PaymentReason1Code": {
      "type": "string",
      "description": "Provides the reason for a specific Payment Status.",
      "enum": [
        "G002",
        "G004",
        "G000",
        "G001",
        "G003"
      ]
    },
    "PaymentTransactionCancellationStatus1": {
      "type": "object",
      "description": "Provides details on the status of the cancellation of a payment transaction.",
      "additionalProperties": false,
      "properties": {
        "transaction_cancellation_status": {
          "description": "Specifies the status of a cancellation request.",
          "$ref": "#/definitions/CancellationIndividualStatus2Code"
        }
      },
      "required": [
        "transaction_cancellation_status"
      ]
    },
    "CancellationIndividualStatus2Code": {
      "type": "string",
      "description": "Specifies the status of a cancellation request.",
      "enum": [
        "CNCL",
        "PDCR",
        "RJCR"
      ]
    },
    "ISODateTime": {
      "type": "string",
      "description": "A particular point in the progression of time defined by a mandatory date and a mandatory time component, expressed in either UTC time format (YYYY-MM-DDThh:mm:ss.sssZ), local time with UTC offset format (YYYY-MM-DDThh:mm:ss.sss+/-hh:mm), or local time format (YYYY-MM-DDThh:mm:ss.sss). These representations are defined in \"XML Schema Part 2: Datatypes Second Edition - W3C Recommendation 28 October 2004\" which is aligned with ISO 8601.\nNote on the time format:\n1) beginning / end of calendar day\n00:00:00 \u003d the beginning of a calendar day\n24:00:00 \u003d the end of a calendar day\n2) fractions of second in time format\nDecimal fractions of seconds may be included. In this case, the involved parties shall agree on the maximum number of digits that are allowed.",
      "pattern": "^(?:[1-9]\\d{3}-(?:(?:0[1-9]|1[0-2])-(?:0[1-9]|1\\d|2[0-8])|(?:0[13-9]|1[0-2])-(?:29|30)|(?:0[13578]|1[02])-31)|(?:[1-9]\\d(?:0[48]|[2468][048]|[13579][26])|(?:[2468][048]|[13579][26])00)-02-29)T(?:[01]\\d|2[0-3]):[0-5]\\d:[0-5]\\d(?:\\.[0-9]+)?(?:Z|[+-][01]\\d:[0-5]\\d)?$"
    },
    "PaymentEvent7": {
      "type": "object",
      "description": "Contains information coming from the related MT 199 or MT 103, or the StatusConfirmationUpdate.",
      "additionalProperties": false,
      "properties": {
        "network_reference": {
          "description": "Reference assigned by the network when sending the payment.",
          "$ref": "#/definitions/Max350Text"
        },
        "message_name_identification": {
          "description": "Specifies the original message name identifier to which the message refers.",
          "$ref": "#/definitions/Max35Text"
        },
        "business_service": {
          "description": "Specifies the business service agreed between the two MessagingEndpoints under which rules this Business Message is exchanged.\r\nTo be used when there is a choice of processing services or processing service levels.",
          "$ref": "#/definitions/Max35Text"
        },
        "tracker_event_type": {
          "description": "Indicates the type of payment event.",
          "$ref": "#/definitions/TrackerEventType1Code"
        },
        "valid": {
          "description": "Indicates if a payment is consistent (YES) or not (NO).",
          "$ref": "#/definitions/YesNoIndicator"
        },
        "invalidity_reason": {
          "description": "Contains the additional information in case the event is not a valid component of a payment transaction.",
          "$ref": "#/definitions/ExternalInvalidPaymentsEvent1Code"
        },
        "invalidity_description": {
          "description": "Contains a textual description as to why the event is not a valid component of a payments transaction.",
          "$ref": "#/definitions/Max350Text"
        },
        "instruction_identification": {
          "description": "Unique identification, as assigned by an instructing party for an instructed party, to unambiguously identify the instruction.\n\nUsage: The instruction identification is a point to point reference that can be used between the instructing party and the instructed party to refer to the individual instruction. It can be included in several messages related to the instruction.",
          "$ref": "#/definitions/Max35Text"
        },
        "related_reference": {
          "description": "Identification referring to the underlying payment transaction.",
          "$ref": "#/definitions/Max35Text"
        },
        "case_identification": {
          "description": "Uniquely identifies the case.",
          "$ref": "#/definitions/Max35Text"
        },
        "original_instruction_identification": {
          "description": "Unique identification, as assigned by the original instructing party for the original instructed party, to unambiguously identify the original instruction.",
          "$ref": "#/definitions/Max35Text"
        },
        "assignment_identification": {
          "description": "Uniquely identifies the case assignment.",
          "$ref": "#/definitions/Max35Text"
        },
        "resolved_case_identification": {
          "description": "Uniquely identifies the case.",
          "$ref": "#/definitions/Max35Text"
        },
        "transaction_status": {
          "description": "Specifies the status of a transaction, in a coded form.",
          "$ref": "#/definitions/PaymentStatus3"
        },
        "underlying_cancellation_details": {
          "description": "Provides further information with regards to the cancellation of the payments event.",
          "$ref": "#/definitions/UnderlyingCancellationDetails2"
        },
        "forwarded_to_agent": {
          "description": "Specifies the BIC of the entity to which the entity reporting the status has forwarded the payment transaction.\r\nThis element can only be used in case the status is ACSP and the reason is G000 or G001.",
          "$ref": "#/definitions/AnyBICIdentifier"
        },
        "funds_available": {
          "description": "Date and time at which the funds are available,  as reported in the ACSC status update.\r\nMust only be used when TransactionStatus is \"ACSC\".",
          "$ref": "#/definitions/ISODateTime"
        },
        "from": {
          "description": "The sending MessagingEndpoint that has created this Business Message for the receiving MessagingEndpoint that will process this Business Message.\r\n\r\nNote\tthe sending MessagingEndpoint might be different from the sending address potentially contained in the transport header (as defined in the transport layer).",
          "$ref": "#/definitions/AnyBICIdentifier"
        },
        "to": {
          "description": "The MessagingEndpoint designated by the sending MessagingEndpoint to be the recipient who will ultimately process this Business Message.\r\n\r\nNote the receiving MessagingEndpoint might be different from the receiving address potentially contained in the transport header (as defined in the transport layer).",
          "$ref": "#/definitions/AnyBICIdentifier"
        },
        "originator": {
          "description": "Party that issues the status.",
          "$ref": "#/definitions/AnyBICIdentifier"
        },
        "serial_parties": {
          "description": "Specifies the parties involved in a serial payment scenario.",
          "$ref": "#/definitions/CustomerCreditTransferTransactionParties1"
        },
        "cover_parties": {
          "description": "Specifies the parties involved in a cover payment scenario.",
          "$ref": "#/definitions/CreditTransferTransactionParties1"
        },
        "sender_acknowledgement_receipt": {
          "description": "Date and time at which the sender was acknowledged receipt of the message.",
          "$ref": "#/definitions/ISODateTime"
        },
        "received_date": {
          "description": "Date and time at which the message was acknowledged by the receiver.",
          "$ref": "#/definitions/ISODateTime"
        },
        "instructed_amount": {
          "description": "Amount of money to be moved between the debtor (ordering customer) and creditor, before deduction of charges, expressed in the currency as ordered by the initiating party.\r\nUsage: This amount has to be transported unchanged through the transaction chain.",
          "$ref": "#/definitions/ActiveOrHistoricCurrencyAndAmount"
        },
        "confirmed_amount": {
          "description": "The amount confirmed by the Originator. Depending on the Transaction Status, this amount can be the credited amount, pending amount, rejected amount or transferred amount.",
          "$ref": "#/definitions/ActiveOrHistoricCurrencyAndAmount"
        },
        "interbank_settlement_amount": {
          "description": "Amount of money moved between the instructing agent and the instructed agent.",
          "$ref": "#/definitions/ActiveCurrencyAndAmount"
        },
        "interbank_settlement_date": {
          "description": "Date on which the amount of money ceases to be available to the agent that owes it and when the amount of money becomes available to the agent to which it is due.",
          "$ref": "#/definitions/ISODate"
        },
        "charge_bearer": {
          "description": "Specifies which party/parties will bear the charges associated with the processing of the payment transaction.",
          "$ref": "#/definitions/ChargeBearerType3Code"
        },
        "charge_amount": {
          "type": "array",
          "description": "Amount of money asked or paid for the charge.",
          "items": {
            "$ref": "#/definitions/ActiveOrHistoricCurrencyAndAmount"
          }
        },
        "foreign_exchange_details": {
          "description": "Specifies the exchange rate details between two currencies.",
          "$ref": "#/definitions/CurrencyExchange8"
        },
        "update_payment": {
          "description": "Implements the correlation between a payment message and a status update on it. \r\nContains the network reference of the payment for which the status confirmation was applicable.",
          "$ref": "#/definitions/Max35Text"
        },
        "duplicate_message_reference": {
          "description": "Network reference of the Payment that is the original recorded Payment.",
          "$ref": "#/definitions/Max50Text"
        },
        "copied_business_service": {
          "description": "Specifies the copied business service of the market infrastructure.",
          "$ref": "#/definitions/Max35Text"
        },
        "last_update_time": {
          "description": "Last date and time at which the status of this Payment event was updated.",
          "$ref": "#/definitions/ISODateTime"
        }
      },
      "required": [
        "network_reference",
        "message_name_identification",
        "tracker_event_type",
        "valid",
        "from",
        "to",
        "sender_acknowledgement_receipt",
        "last_update_time"
      ]
    },
    "Max350Text": {
      "type": "string",
      "description": "Specifies a character string with a maximum length of 350 characters.",
      "minLength": 1,
      "maxLength": 350
    },
    "Max35Text": {
      "type": "string",
      "description": "Specifies a character string with a maximum length of 35 characters.",
      "minLength": 1,
      "maxLength": 35
    },
    "TrackerEventType1Code": {
      "type": "string",
      "description": "Indicates the type of payment event.",
      "enum": [
        "COPT",
        "CTPT",
        "CTCA",
        "CTTS",
        "CTCR",
        "COSU",
        "CTSU"
      ]
    },
    "YesNoIndicator": {
      "type": "boolean",
      "description": "Indicates a \"Yes\" or \"No\" type of answer for an element."
    },
    "ExternalInvalidPaymentsEvent1Code": {
      "type": "string",
      "description": "Specifies the reason why a payments event is an invalid component of a payments transaction."
    },
    "UnderlyingCancellationDetails2": {
      "type": "object",
      "description": "Provides details on the status of the cancellation of the underlying payments event.",
      "additionalProperties": false,
      "properties": {
        "cancellation_request_details": {
          "description": "Provides information from the cancellation request.",
          "$ref": "#/definitions/CancellationRequestDetails1"
        },
        "cancellation_response_details": {
          "description": "Provides information from the cancellation response.",
          "$ref": "#/definitions/CancellationResponseDetails1"
        },
        "cancellation_request_tracking_status": {
          "description": "Specifies the status of the cancellation request.",
          "$ref": "#/definitions/PaymentCancellationStatusReason2Code"
        }
      }
    },
    "CancellationRequestDetails1": {
      "type": "object",
      "description": "Contains detailed information from the cancellation request (e.g. an MT 192 or an MT 199).",
      "additionalProperties": false,
      "properties": {
        "cancellation_reason_information": {
          "description": "Contains information on the reason for the cancellation.",
          "$ref": "#/definitions/CancellationReason6Code"
        },
        "indemnity_agreement": {
          "$ref": "#/definitions/PendingPaymentCancellationReason2Code"
        }
      },
      "required": [
        "cancellation_reason_information"
      ]
    },
    "CancellationReason6Code": {
      "type": "string",
      "description": "Provides the possible reasons why a payment is to be cancelled.",
      "enum": [
        "DUPL",
        "AGNT",
        "CURR",
        "CUST",
        "UPAY",
        "CUTA",
        "TECH",
        "FRAD",
        "AMNT",
        "COVR"
      ]
    },
    "PendingPaymentCancellationReason2Code": {
      "type": "string",
      "description": "Provides the reason as to why a payment cancellation request is pending.",
      "enum": [
        "INDM"
      ]
    },
    "CancellationResponseDetails1": {
      "type": "object",
      "description": "Contains detailed information from the cancellation response (e.g. an MT 196 or an MT 199).",
      "additionalProperties": false,
      "properties": {
        "investigation_execution_status": {
          "description": "Provides the status on the execution of an investigation.",
          "$ref": "#/definitions/InvestigationExecutionConfirmation5Code"
        },
        "investigation_execution_status_reason": {
          "$ref": "#/definitions/InvestigationExecutionStatusReason1"
        }
      },
      "required": [
        "investigation_execution_status"
      ]
    },
    "InvestigationExecutionConfirmation5Code": {
      "type": "string",
      "description": "Provides the status on an investigation.",
      "enum": [
        "CNCL",
        "PDCR",
        "RJCR"
      ]
    },
    "InvestigationExecutionStatusReason1": {
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "rejected": {
          "$ref": "#/definitions/PaymentCancellationRejection3Code"
        },
        "pending": {
          "$ref": "#/definitions/PendingPaymentCancellationReason1Code"
        }
      }
    },
    "PaymentCancellationRejection3Code": {
      "type": "string",
      "description": "Returned when a request for cancellation cannot be executed.",
      "enum": [
        "AGNT",
        "ARDT",
        "INDM",
        "AC04",
        "CUST",
        "AM04",
        "LEGL",
        "NOAS",
        "NOOR"
      ]
    },
    "PendingPaymentCancellationReason1Code": {
      "type": "string",
      "description": "Provides the reason as to why a payment cancellation request is pending.",
      "enum": [
        "INDM",
        "PTNA",
        "RQDA"
      ]
    },
    "PaymentCancellationStatusReason2Code": {
      "type": "string",
      "enum": [
        "S004",
        "S003",
        "S002",
        "S000",
        "S001"
      ]
    },
    "AnyBICIdentifier": {
      "type": "string",
      "description": "Code allocated to a financial or non-financial institution by the ISO 9362 Registration Authority, as described in ISO 9362 \"Banking - Banking telecommunication messages - Business identifier code (BIC)\".",
      "pattern": "^[A-Z]{6,6}[A-Z2-9][A-NP-Z0-9]([A-Z0-9]{3,3}){0,1}$"
    },
    "CustomerCreditTransferTransactionParties1": {
      "type": "object",
      "description": "Specifies all the parties involved in a serial payment transaction.",
      "additionalProperties": false,
      "properties": {
        "debtor_agent": {
          "description": "Financial institution servicing an account for the debtor.",
          "$ref": "#/definitions/AnyBICIdentifier"
        },
        "instructing_reimbursement_agent": {
          "description": "Agent through which the instructing agent (identified in the \"From\" element) will reimburse the instructed agent (identified in the \"To\" element).",
          "$ref": "#/definitions/AnyBICIdentifier"
        },
        "creditor_agent": {
          "description": "Financial institution servicing an account for the creditor.",
          "$ref": "#/definitions/AnyBICIdentifier"
        }
      }
    },
    "CreditTransferTransactionParties1": {
      "type": "object",
      "description": "Specifies all the parties involved in a cover payment transaction.",
      "additionalProperties": false,
      "properties": {
        "debtor": {
          "description": "Financial institution that owes an amount of money to the (ultimate) financial institutional creditor.",
          "$ref": "#/definitions/AnyBICIdentifier"
        },
        "debtor_agent": {
          "description": "Financial institution servicing an account for the debtor.",
          "$ref": "#/definitions/AnyBICIdentifier"
        },
        "intermediary_agent1": {
          "description": "Agent between the debtor\u0027s agent and the creditor\u0027s agent.",
          "$ref": "#/definitions/AnyBICIdentifier"
        },
        "creditor_agent": {
          "description": "Financial institution servicing an account for the creditor.",
          "$ref": "#/definitions/AnyBICIdentifier"
        },
        "creditor": {
          "description": "Financial institution that receives an amount of money from the financial institutional debtor.",
          "$ref": "#/definitions/AnyBICIdentifier"
        }
      }
    },
    "ActiveOrHistoricCurrencyAndAmount": {
      "type": "object",
      "description": "A number of monetary units specified in an active or a historic currency where the unit of currency is explicit and compliant with ISO 4217.",
      "properties": {
        "currency": {
          "$ref": "#/definitions/ActiveOrHistoricCurrencyCode"
        },
        "Amount": {
          "type": "string",
          "maxLength": 19
        }
      }
    },
    "ActiveOrHistoricCurrencyCode": {
      "type": "string",
      "description": "A code allocated to a currency by a Maintenance Agency under an international identification scheme, as described in the latest edition of the international standard ISO 4217 \"Codes for the representation of currencies and funds\".",
      "pattern": "^[A-Z]{3,3}$"
    },
    "ActiveCurrencyAndAmount": {
      "type": "object",
      "description": "A number of monetary units specified in an active currency where the unit of currency is explicit and compliant with ISO 4217.",
      "properties": {
        "currency": {
          "$ref": "#/definitions/ActiveCurrencyCode"
        },
        "Amount": {
          "type": "string",
          "maxLength": 19
        }
      }
    },
    "ActiveCurrencyCode": {
      "type": "string",
      "description": "A code allocated to a currency by a Maintenance Agency under an international identification scheme as described in the latest edition of the international standard ISO 4217 \"Codes for the representation of currencies and funds\".",
      "pattern": "^[A-Z]{3,3}$"
    },
    "ISODate": {
      "type": "string",
      "description": "A particular point in the progression of time in a calendar year expressed in the YYYY-MM-DD format. This representation is defined in \"XML Schema Part 2: Datatypes Second Edition - W3C Recommendation 28 October 2004\" which is aligned with ISO 8601.",
      "pattern": "^(?:[1-9]\\d{3}-(?:(?:0[1-9]|1[0-2])-(?:0[1-9]|1\\d|2[0-8])|(?:0[13-9]|1[0-2])-(?:29|30)|(?:0[13578]|1[02])-31)|(?:[1-9]\\d(?:0[48]|[2468][048]|[13579][26])|(?:[2468][048]|[13579][26])00)-02-29)$"
    },
    "ChargeBearerType3Code": {
      "type": "string",
      "description": "Specifies which party/parties will bear the charges associated with the processing of the payment transaction.",
      "enum": [
        "SHAR",
        "DEBT",
        "CRED"
      ]
    },
    "CurrencyExchange8": {
      "type": "object",
      "description": "Set of elements used to provide details of the currency exchange.",
      "additionalProperties": false,
      "properties": {
        "source_currency": {
          "description": "Currency from which an amount is to be converted in a currency conversion.",
          "$ref": "#/definitions/ActiveOrHistoricCurrencyCode"
        },
        "target_currency": {
          "description": "Currency into which an amount is to be converted in a currency conversion.",
          "$ref": "#/definitions/ActiveOrHistoricCurrencyCode"
        },
        "exchange_rate": {
          "description": "Factor used to convert an amount from one currency into another. This reflects the price at which one currency was bought with another currency.\n\nUsage: ExchangeRate expresses the ratio between UnitCurrency and QuotedCurrency (ExchangeRate \u003d UnitCurrency/QuotedCurrency).",
          "$ref": "#/definitions/BaseOneRate"
        }
      },
